import React from 'react'
import {motion} from "framer-motion"
import AnchorLink from 'react-anchor-link-smooth-scroll'
import useMediaQuery from '../hooks/useMediaQuery'
import profile from '../assets/profile-image.png'

const Landing = ({setselectedPage}) => {
    const isAboveMediumScreens = useMediaQuery("(min-width: 1060px)");
  return (
   <section id='home' className='md:flex md:justify-between md:items-center md:h-full gap-16 py-10'>

    {/* Image section */}
    <div className='md:order-2 flex justify-center basis-3/5 z-10 mt-16 md:mt-32'>
        {isAboveMediumScreens ? (
            <div 
            className='
            relative z-0 ml-20 before:absolute before:-top-20 before:-left-20 before:rounded-t-[400px] before:w-full before:max-w-[400px]
            before:h-full before:border-2 before:border-blue before:z-[-1]'
            // className="relative z-0 ml-20 before:absolute before:-top-20 before:-left-20 before:rounded-t-[400px] before:w-full before:max-w-[400px] before:h-full before:border-2 before:border-blue before:z-[-1]"
            >

                <img src={profile} alt='profile-image'
                className='hover:filter hover:saturate-200 transition duration-500 z-10 w-full 
                max-w-[400px] md:max-w-[600px]
                '
                >
                </img>
            </div>
        ) : (
            <div></div>
        ) }

    </div>

   </section>
  )
}

export default Landing
