import { useEffect, useState } from "react";
import DotGroup from './scenes/DotGroup.jsx'
import useMediaQuery from './hooks/useMediaQuery'
import Navbar from "./scenes/Navbar";
import Landing from "./scenes/Landing.jsx";
import LineGradient from "./components/LineGradient"
import Skills from './scenes/Skills'

function App() {
  const [selectedPage, setselectedPage] = useState('home')
  const isAboveMediumScreens = useMediaQuery("(min-width : 1060px)")
  const [isTopOfPage, setisTopOfPage] = useState(true)


  useEffect(() => {
    const handleScroll = () => {
      if(window.scrollY === 0 ) setisTopOfPage(true);
      if(window.scrollY !== 0 ) setisTopOfPage(false);
    }
    window.addEventListener('scroll',handleScroll)
    return () => window.removeEventListener('scroll',handleScroll)
  })

  return (
    <div className="app bg-deep-blue ">
    <Navbar isTopOfPage={isTopOfPage} selectedPage={selectedPage} setselectedPage={setselectedPage}></Navbar>
    <div className="w-5/6 mx-auto md:h-full mb-6 ">
      {isAboveMediumScreens && (
        <DotGroup 
        selectedPage={selectedPage}
        setselectedPage={setselectedPage}
        />
      )}
      <Landing setselectedPage={setselectedPage} />
      </div>
      <LineGradient/>
      <div className="w-5/6 mx-auto md:h-full ">
        <Skills />
      </div>
    </div>
  );
}

export default App;
