import React from 'react'
import AnchorLink from 'react-anchor-link-smooth-scroll'

const DotGroup = ({selectedPage, setselectedPage}) => {
    const selectedStyles = `relative bg-yellow 
    before:absolute before:h-6 before:rounded-full before:border-2 before:border-yellow before:left[50%] before:top-[-50%]

    `
  return (
    <div className='flex flex-col gap-6 fixed top-[60%] right-7'>
    <AnchorLink className={`${selectedPage === 'home' ? "text-yellow" :  " bg-dark-grey  text-white"}
            w-3 h-3 rounded-full`}
            href={`#home`}
            onClick={() => setselectedPage('home')}
            >
    </AnchorLink>
    </div>
  )
}

export default DotGroup
